import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class New extends Component {
      constructor(){
      super();
      console.log("Hello this is news component")
      this.state={
        articles:[],
        loading:false,
        page:1
      }
    }

    async componentDidMount(){
      console.log('cdm')
      let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=5cef0d8d47b140d4ae6a5fdc378af54f&page=1"
      let data=await fetch(url)
      let parsedData=await data.json()
      console.log(parsedData);
      this.setState({articles: parsedData.articles})
    }
  render() {
    return (
      <div className='container my-3'>
        <h2>News Headline</h2>
        <div className="row">
          {this.state.articles.map((element)=>{
            return   <div className="col-md-4" key={element.url}>
            <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0, 88):""} imageUrl={element.urlToImage} newsUrl={element.url}/>
          </div>
          })}

          <div className="container d-flex justify-content-between">
          <button disabled="page<=1"type="button" className="btn btn-dark">&larr; Previous</button>
          <button type="button" className="btn btn-dark">Next &rarr;</button>
          </div>
        </div>
      </div>
    )
  }
}

export default New
