import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Loading from './Loading';
import PropTypes from 'prop-types';

export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 6,
    category: 'general',
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0,
    };
  }

  async fetchNews(page) {
    try {
      const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=5cef0d8d47b140d4ae6a5fdc378af54f&page=${page}&pageSize=${this.props.pageSize}`;
      this.setState({ loading: true });
      const response = await fetch(url);
      const parsedData = await response.json();

      if (parsedData.status === 'ok') {
        this.setState({
          articles: parsedData.articles,
          totalResults: parsedData.totalResults,
          loading: false,
        });
      } else {
        console.error('Error fetching data:', parsedData.message);
        this.setState({ loading: false });
      }
    } catch (error) {
      console.error('Fetch error:', error);
      this.setState({ loading: false });
    }
  }

  async componentDidMount() {
    console.log('ComponentDidMount called');
    await this.fetchNews(this.state.page);
  }

  handleNextClick = async () => {
    console.log('Next button clicked');
    if (this.state.page + 1 <= Math.ceil(this.state.totalResults / this.props.pageSize)) {
      await this.fetchNews(this.state.page + 1);
      this.setState({ page: this.state.page + 1 });
    }
  };

  handlePrevClick = async () => {
    console.log('Previous button clicked');
    if (this.state.page > 1) {
      await this.fetchNews(this.state.page - 1);
      this.setState({ page: this.state.page - 1 });
    }
  };

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">News Headlines</h2>
        {this.state.loading && <Loading />}
        <div className="row">
          {!this.state.loading &&
            this.state.articles.map((article) => (
              <div className="col-md-4" key={article.url}>
                <NewsItem
                  title={article.title ? article.title.slice(0, 45) : 'No Title'}
                  description={article.description ? article.description.slice(0, 88) : 'No Description'}
                  imageUrl={article.urlToImage || 'https://via.placeholder.com/150'}
                  newsUrl={article.url}
                  author={article.author || 'Unknown'}
                  publishedAt={article.publishedAt || 'Unknown'}
                />
              </div>
            ))}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            &larr; Previous
          </button>
          <button
            disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)}
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
