import React, { Component } from 'react';
import { Link } from "react-router-dom";
import countries from '../countries.json'; // Import the countries JSON file

export default class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCountry: 'in', // Default country
    };
  }

  handleCountryChange = (event) => {
    this.setState({ selectedCountry: event.target.value });
    this.props.onCountrySearch(event.target.value); // Call the parent's method
  };

  render() {
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Daily News</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/business">Business</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/entertainment">Entertainment</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/general">General</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/health">Health</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/science">Science</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/sports">Sports</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/technology">Technology</Link></li>
            </ul>
            {/* Dynamic Dropdown for Country Selection */}
            <form className="d-flex">
              <select
                className="form-select me-2"
                value={this.state.selectedCountry}
                onChange={this.handleCountryChange}
              >
                {countries.map((country) => (
                  <option key={country.code} value={country.code}>
                    {country.name}
                  </option>
                ))}
              </select>
            </form>
          </div>
        </div>
      </nav>
    );
  }
}
