import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Loading from './Loading';
import PropTypes from 'prop-types';

export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 6,
    category: 'general'
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0
    };
  }

  async componentDidMount() {
    this.fetchNews(); // Fetch news initially
  }

  // Function to fetch news data based on the current country, category, and page
  fetchNews = async () => {
    const { country, category, pageSize } = this.props;
    const { page } = this.state;
    
    this.setState({ loading: true });

    // Correct URL construction
    const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=5cef0d8d47b140d4ae6a5fdc378af54f&page=${page}&pageSize=${pageSize}`;
    
    let data = await fetch(url);
    let parsedData = await data.json();
    
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false
    });
  }

  handlenextclick = async () => {
    const { page, totalResults } = this.state;
    const { pageSize } = this.props;

    if (page + 1 <= Math.ceil(totalResults / pageSize)) {
      this.setState({ page: page + 1 }, this.fetchNews); // Increment page and fetch news again
    }
  };

  handleprevclick = async () => {
    const { page } = this.state;
    if (page > 1) {
      this.setState({ page: page - 1 }, this.fetchNews); // Decrement page and fetch news again
    }
  };

  render() {
    return (
      <div className='container my-3'>
        <h2>News Headline</h2>
        {this.state.loading && <Loading />}
        <div className="row">
          {!this.state.loading && this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 45) : ""}
                  description={element.description ? element.description.slice(0, 88) : ""}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                  author={element.author}
                  publishedAt={element.publishedAt}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button 
            disabled={this.state.page <= 1} 
            type="button" 
            className="btn btn-dark" 
            onClick={this.handleprevclick}
          >
            &larr; Previous
          </button>
          <button 
            disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)} 
            type="button" 
            className="btn btn-dark" 
            onClick={this.handlenextclick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
